import React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
// import TableContainer from '@mui/material/TableContainer';
// import Paper from '@mui/material/Paper';
// import Table from '@mui/material/Table';
// import TableBody from '@mui/material/TableBody';
// import TableRow from '@mui/material/TableRow';
import Button from '@mui/material/Button';
// import TableCell from '@mui/material/TableCell';
// import Stack from '@mui/material/Stack';
import Avatar from '@mui/material/Avatar';
import {useState} from 'react';
import ClearIcon from '@mui/icons-material/Clear';
// import Star from '@mui/icons-material/Star';
import StarBorder from '@mui/icons-material/StarBorder';

const drawerWidth = 200;

const fetchMails = (setMails, setError, mailbox) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/Mailbox?mailbox='+mailbox, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};

/**
 * @param {string} props prop for function
 * @return {object} JSX Table
 */
function Home(props: Props) {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');

  const {window} = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [inbox, setInbox] = useState('Inbox');
  const [title, setTitle] = useState('Inbox');

  const [display, setDisplay] = useState(false);
  const [subject, setSubject] = useState('');
  const [from, setFrom] = useState('');
  const [received, setReceived] = useState('');
  const [content, setContent] = useState('');
  const [fromAddress, setFromAddress] = useState('');
  const [toAddress, setToAddress] = useState('');
  const [to, setTo] = useState('');

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError, 'Inbox');
  }, []);

  // Handles Inbox press, updates category and title
  const handleInbox = () => {
    setInbox('Inbox');
    setTitle('Inbox');
    if (inbox==='Trash') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Inbox');
  };
  // Handles Trash press, updates category and title
  const handleTrash = () => {
    setInbox('Trash');
    setTitle('Trash');
    if (inbox==='Inbox') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Trash');
  };

  // Drawer component on left side, definitions here.
  // Following 25 lines taken from https://mui.com/material-ui/react-drawer/
  const drawer = (
    <div>
      <Toolbar />
      <Divider />
      <List>
        <ListItem key={'Inbox'} disablePadding>
          <ListItemButton onClick={()=>handleInbox()}>
            <ListItemIcon>
              {0 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Inbox'} />
          </ListItemButton>
        </ListItem>
        <ListItem key={'Trash'} disablePadding>
          <ListItemButton onClick={()=>handleTrash()}>
            <ListItemIcon>
              {1 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Trash'} />
          </ListItemButton>
        </ListItem>
      </List>
      <Divider />
    </div>
  );
  // Defines container, the window being populated
  // following 2 lines taken from https://mui.com/material-ui/react-drawer/
  const container = window !== undefined ? () =>
    window().document.body : undefined;

  /**
   * Creates display to show email contents when clicking on specific
   * email. Code for box and appbar is influenced by the code found on
   * https://mui.com/material-ui/react-drawer/
   * Similar style to title appbar and box
   * @return {*} String containing display elements
   */
  const createDisplay = () => {
    return (
      <Box z-index='appBar'
        hidden={!display}
        sx={{
          flexgrow: 1,
          height: '95%',
          width: {sm: `calc(100%)`},
          position: 'fixed',
          bottom: 0,
          right: 0,
          backgroundColor: 'white'}}>
        <AppBar
          sx={{
            flexgrow: 1,
            height: '60px',
            width: {sm: `calc(100%)`},
            position: 'fixed',
            top: '0%',
            right: 0,
            backgroundColor: 'primary.dark'}}
        >
          <Toolbar>
            <Typography variant='h4' component='div'
              sx={{flexGrow: 1,
                color: 'white'}} display='inline'>
              {subject}
            </Typography>
            <IconButton
              size='large'
              color='inherit'
              aria-label='close desktop reader'
              onClick={()=>setDisplay(false)}><ClearIcon/></IconButton>
          </Toolbar>
        </AppBar>
        <Typography variant='body1'
          sx={{color: 'white'}}>
          {'From: ' + from + ' (' + fromAddress + ')\n'}
        </Typography>
        <Typography>
          {'To: ' + to + ' (' + toAddress + ')\n'}
        </Typography>
        <Typography>
          {'Subject: ' + subject + '\n'}
        </Typography>
        <Typography>
          {'Received: ' + received + '\n\n'}
        </Typography>
        <Typography>
          {content}
        </Typography>
        <Button
          aria-label='close mobile reader'
          onClick={()=>{
            setDisplay(false);
          }}></Button>
      </Box>
    );
  };
  /**
   * Updates display with the contents of an email
   * Also sets display to true
   * @param {string} mail email being used to update display
   * @return {*} Returns nothing, only needs to update display
   */
  const updateDisplay = (mail) => {
    setDisplay(true);
    setSubject(mail.mail.subject);
    setFrom(mail.mail.from.name);
    setFromAddress(mail.mail.from.address);
    setTo(mail.mail.to.name);
    setToAddress(mail.to.mail.address);
    setContent(mail.mail.content);
    const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    let e = '';
    const current = new Date(mail.mail.received);
    e=months[current.getMonth()]+' '+current.getDate()+', '+
      current.getFullYear()+' @ '+current.getHours()+':'+current.getMinutes();
    setReceived(e);
    return '';
  };

  /**
   * creates  row for the current email being looked at
   * Determines the category the email is in from email.mailbox
   * Has onClick function to display email contents and
   * getReceived() function to determine display dates
   * @param {string} mail email row is being created for
   * @return {string} String containing TableRow elements
   */
  const createRow = (mail) => {
    let item = localStorage.getItem('user');
    item = JSON.parse(item);
    let s = ' - ' + mail.mail.content + 'aaaaaaaaaaaaaaaaaaaaa'+
    'aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa';
    if (mail.mail.subject.length*0.3 + s.length>40) {
      s = s.substring(0, 40-mail.mail.subject.length) + '...';
    }
    // https://mui.com/material-ui/react-list/
    if (item['name'] === mail.mailbox['Name']) {
      return (
        <Grid item xs={12}>
          <ListItemButton onClick={()=>updateDisplay(mail)}
            sx={{flexGrow: 1}} alignItems="flex-start">
            <Avatar/>
            <ListItemText primary={mail.mail.from.name}
              secondary={
                <React.Fragment>
                  <Typography
                    sx={{display: 'inline'}}
                    component="span"
                    variant="body2"
                    color="text.primary"
                  >
                    {mail.mail.subject}
                  </Typography>
                  {s}
                </React.Fragment>
              }></ListItemText>
            {getReceived(mail.mail.received)}
            <StarBorder />
          </ListItemButton>
        </Grid>
      );
    } else {
      console.log(error);
      return;
    }
  };

  /**
   * Updates the received date to proper display for table cell
   * @param {string} received Received string to convert to date
   * @return {string} returns string to put in cell.
   */
  const getReceived = (received) => {
    const d = new Date(received);
    const current = new Date();
    const months = [
      'Jan',
      'Feb',
      'Mar',
      'Apr',
      'May',
      'Jun',
      'Jul',
      'Aug',
      'Sep',
      'Oct',
      'Nov',
      'Dec',
    ];

    if (current.getFullYear() - d.getFullYear()>0) { // Greater than one year
      return d.getFullYear();
    } else if (current.getMonth() - d.getMonth() !== 0 ||
    current.getDate() - d.getDate() !== 0) { // Greater than one month
      if (Number(d.getDate())<10) {
        return months[d.getMonth()] + ' 0' + d.getDate();
      }
      return months[d.getMonth()] + ' ' + d.getDate();
    } else if (Number(d.getHours())<10) { // Same day
      return '0'+d.getHours()+':'+d.getMinutes();
    } else {
      return d.getHours()+':'+d.getMinutes();
    }
  };

  return (
    <div>
      {/* Box and all code inside taken except title taken from https://mui.com/material-ui/react-drawer/*/}
      <Box sx={{flexGrow: 1}}>
        <CssBaseline />
        <AppBar
          position="static"
        >
          <Box sx={{display: 'flex', justifyContent: 'space-between'}}>
            <Toolbar>
              <IconButton
                color="inherit"
                aria-label="toggle drawer"
                edge="start"
                onClick={handleDrawerToggle}
                sx={{mr: 2}}
              >
                <MenuIcon />
              </IconButton>
              <Typography>
                {name}
              </Typography>
            </Toolbar>
            <Button onClick={logout} href='/Login'>
              <Avatar/></Button>
          </Box>
        </AppBar>
        <Box sx={{bgcolor: '#eff1f1'}}>
          <Typography variant="h4" sx={{p: 2}}>
            {title}
          </Typography>
        </Box>
        {/* Box taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="nav"
          sx={{width: {sm: drawerWidth}, flexShrink: {sm: 0}}}
          aria-label="mailbox folders"
        >
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            container = {container}
            variant="temporary"
            sx={{
              'display': {xs: 'none', sm: 'block'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
            open={mobileOpen}
            onClose={handleDrawerToggle}
          >
            {drawer}
          </Drawer>
        </Box>
        {/* Box declaration and attributes taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="main"
          sx={{flexGrow: 1}}
        >
          <Toolbar />
          <Grid container spacing={2}>
            {mails.map((mail) => (createRow(mail)
            ))}
            {createDisplay()}
          </Grid>
        </Box>
      </Box>
    </div>
  );
}

