import React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import {styled, alpha} from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import NavigateBeforeIcon from '@mui/icons-material/NavigateBefore';
// import TableContainer from '@mui/material/TableContainer';
// import Paper from '@mui/material/Paper';
// import Table from '@mui/material/Table';
// import TableBody from '@mui/material/TableBody';
// import TableRow from '@mui/material/TableRow';
import Button from '@mui/material/Button';
// import TableCell from '@mui/material/TableCell';
// import Stack from '@mui/material/Stack';
import Avatar from '@mui/material/Avatar';
import {useState} from 'react';
// import ClearIcon from '@mui/icons-material/Clear';
// import Star from '@mui/icons-material/Star';
import StarBorder from '@mui/icons-material/StarBorder';
import DeleteIcon from '@mui/icons-material/Delete';
import MoveToInboxIcon from '@mui/icons-material/MoveToInbox';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import StarIcon from '@mui/icons-material/Star';

const drawerWidth = 200;

const fetchMails = (setMails, setError, mailbox) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/Mailbox?mailbox='+mailbox, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};

/**
 * @param {string} props prop for function
 * @return {object} JSX Table
 */
function Home(props: Props) {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');

  const {window} = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [inbox, setInbox] = useState('Inbox');
  const [title, setTitle] = useState('Inbox');

  const [display, setDisplay] = useState(false);
  const [subject, setSubject] = useState('');
  const [from, setFrom] = useState('');
  const [received, setReceived] = useState('');
  const [content, setContent] = useState('');
  const [favorite, setFavorite] = useState('');
  const [fromAddress, setFromAddress] = useState('');
  // const [read, setRead] = useState(600);
  // const [toAddress, setToAddress] = useState('');
  // const [to, setTo] = useState('');

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError, 'Inbox');
  }, []);

  // Handles Inbox press, updates category and title
  const handleInbox = () => {
    setInbox('Inbox');
    setTitle('Inbox');
    if (inbox==='Trash') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Inbox');
  };
  // Handles Trash press, updates category and title
  const handleTrash = () => {
    setInbox('Trash');
    setTitle('Trash');
    if (inbox==='Inbox') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Trash');
  };

  // Code for search below taken from this
  // https://mui.com/material-ui/react-app-bar/
  const Search = styled('div')(({theme}) => ({
    'position': 'relative',
    'borderRadius': theme.shape.borderRadius,
    'backgroundColor': alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    'marginRight': theme.spacing(2),
    'marginLeft': 0,
    'width': '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }));

  const SearchIconWrapper = styled('div')(({theme}) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }));

  const StyledInputBase = styled(InputBase)(({theme}) => ({
    'color': 'inherit',
    '& .MuiInputBase-input': {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('md')]: {
        width: '20ch',
      },
    },
  }));

  // Drawer component on left side, definitions here.
  // Following 25 lines taken from https://mui.com/material-ui/react-drawer/
  const drawer = (
    <div>
      <Toolbar />
      <ListItem>
        <Typography variant ='h6'>
          {name}
        </Typography>
      </ListItem>
      <Divider />
      <List>
        <ListItem key={'Inbox'} disablePadding>
          <ListItemButton onClick={()=>handleInbox()}>
            <ListItemIcon>
              {0 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Inbox'} />
          </ListItemButton>
        </ListItem>
        <ListItem key={'Trash'} disablePadding>
          <ListItemButton onClick={()=>handleTrash()}>
            <ListItemIcon>
              {1 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Trash'} />
          </ListItemButton>
        </ListItem>
      </List>
      <Divider />
    </div>
  );
  // Defines container, the window being populated
  // following 2 lines taken from https://mui.com/material-ui/react-drawer/
  const container = window !== undefined ? () =>
    window().document.body : undefined;

  /**
   * Creates display to show email contents when clicking on specific
   * email. Code for box and appbar is influenced by the code found on
   * https://mui.com/material-ui/react-drawer/
   * Similar style to title appbar and box
   * @return {*} String containing display elements
   */
  const createDisplay = () => {
    return (
      <Box z-index='appBar'
        hidden={!display}
        sx={{
          flexgrow: 1,
          height: '95%',
          width: {sm: `calc(100%)`},
          position: 'fixed',
          bottom: 0,
          right: 0,
          backgroundColor: 'white'}}>
        <AppBar
          sx={{
            flexgrow: 1,
            height: '55px',
            width: {sm: `calc(100%)`},
            position: 'fixed',
            top: '0%',
            right: 0,
            backgroundColor: 'primary.dark'}}
        >
          <Toolbar>
            <NavigateBeforeIcon aria-label='close mobile reader'
              fontSize = 'large'
              onClick={()=>{
                setDisplay(false);
              }}/>
            <Typography variant='h4' component='div'
              sx={{flexGrow: 1}}>
            </Typography>
            <MailOutlineIcon fontSize = 'large'/>
            <MoveToInboxIcon fontSize = 'large'/>
            <DeleteIcon fontSize = 'large'/>
          </Toolbar>
        </AppBar>
        <Typography variant='h4' component='div'
          sx={{flexGrow: 1,
            borderTop: 1,
            color: '#000000',
            backgroundColor: '#E5E5E5'}}>
          {subject}
        </Typography>
        <Box sx={{backgroundColor: '#E0E0E0'}}>
          <ListItem
            sx={{flexGrow: 1, backgroundColor: '#E0E0E0',
              borderBottom: 1,
              borderTop: 1}}>
            <Typography variant = 'h5'>{title}</Typography>
            <ListItemText></ListItemText>
            <StarBorder />
          </ListItem>
          <ListItem
            sx={{flexGrow: 1, backgroundColor: '#F0F0F0',
              borderBottom: 1}}
            alignItems="center">
            <Avatar/>
            <ListItemText primary={from + ' ' + getReceived(received)}
              secondary={
                <React.Fragment>
                  <Typography
                    sx={{display: 'inline'}}
                    component="span"
                    variant="body2"
                    color="text.primary"
                  >
                    {fromAddress}
                  </Typography>
                </React.Fragment>
              }></ListItemText>
            <ArrowBackIcon />
          </ListItem>
        </Box>
        <Typography variant='body1'>
          {content}
        </Typography>
      </Box>
    );
  };
  /**
   * Updates display with the contents of an email
   * Also sets display to true
   * @param {string} mail email being used to update display
   * @return {*} Returns nothing, only needs to update display
   */
  const updateDisplay = (mail) => {
    mail.mail.read = 300;
    setDisplay(true);
    setSubject(mail.mail.subject);
    setFrom(mail.mail.from.name);
    setFromAddress(mail.mail.from.email);
    // setTo(mail.mail.to.name);
    // setToAddress(mail.mail.to.email);
    setContent(mail.mail.content);
    const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    let e = '';
    const current = new Date(mail.mail.received);
    e=months[current.getMonth()]+' '+current.getDate()+', '+
      current.getFullYear()+' @ '+current.getHours()+':'+current.getMinutes();
    setReceived(e);
    return '';
  };

  /**
   * creates  row for the current email being looked at
   * Determines the category the email is in from email.mailbox
   * Has onClick function to display email contents and
   * getReceived() function to determine display dates
   * @param {string} mail email row is being created for
   * @return {string} String containing TableRow elements
   */
  const createRow = (mail) => {
    let item = localStorage.getItem('user');
    item = JSON.parse(item);
    let from = mail.mail.from.name;
    if (from.length > 25) {
      from = from.substring(0, 22) + '...';
    }
    let s = mail.mail.subject + ' - ' + mail.mail.content;
    if (s.length>50) {
      s = s.substring(0, 47) + '...';
    }
    // https://mui.com/material-ui/react-list/
    if (item['name'] === mail.mailbox['Name']) {
      return (
        <Grid item xs={12}>
          <ListItem // onClick={()=>updateDisplay(mail)}
            sx={{flexGrow: 1, backgroundColor: '#F0F0F0',
              border: 2}}
            alignItems="flex-start">
            <Avatar/>
            <ListItemText onClick={()=>updateDisplay(mail)}
            primary={
              <React.Fragment>
                <Typography
                  sx={{fontWeight: mail.mail.read}}
                >
                  {from}
                </Typography>
              </React.Fragment>
            }
            secondary={
              <React.Fragment>
                <Typography
                  sx={{display: 'inline',
                    fontWeight: mail.mail.read}}
                  component="span"
                  variant="body2"
                  color="text.primary"
                >
                  {s}
                </Typography>
              </React.Fragment>
            }></ListItemText>
            <Typography sx={{fontWeight: mail.mail.read}}>
              {getReceived(mail.mail.received)}
            </Typography>
            <StarIcon style={{color: mail.mail.favorite}}
              onClick={()=>{
                if (mail.mail.favorite === 'grey') {
                  setFavorite('yellow');
                  console.log(favorite);
                  mail.mail.favorite = 'yellow';
                  return;
                } else {
                  setFavorite('grey');
                  console.log(favorite);
                  mail.mail.favorite ='grey';
                  return;
                }
              }}/>
          </ListItem>
        </Grid>
      );
    } else {
      console.log(error);
      return;
    }
  };

  /**
   * Updates the received date to proper display for table cell
   * @param {string} received Received string to convert to date
   * @return {string} returns string to put in cell.
   */
  const getReceived = (received) => {
    const d = new Date(received);
    const current = new Date();
    const months = [
      'Jan',
      'Feb',
      'Mar',
      'Apr',
      'May',
      'Jun',
      'Jul',
      'Aug',
      'Sep',
      'Oct',
      'Nov',
      'Dec',
    ];

    if (current.getFullYear() - d.getFullYear()>0) { // Greater than one year
      return d.getFullYear();
    } else if (current.getMonth() - d.getMonth() !== 0 ||
        Math.abs(current.getDate() - d.getDate()) > 1) { // G than one month
      if (Number(d.getDate())<10) {
        return months[d.getMonth()] + ' 0' + d.getDate();
      }
      return months[d.getMonth()] + ' ' + d.getDate();
    } else if (current.getDate() - d.getDate() === 1) { // Yesterday
      return 'Yesterday';
    } else {
      const hours = d.getHours() % 12;
      const minutes = d.getMinutes() < 10 ? '0'+d.getMinutes() : d.getMinutes();
      const ampm = d.getHours()>=12 ? 'PM' : 'AM';
      return hours + ':' + minutes + ' ' + ampm;
    }
  };

  return (
    <div>
      {/* Box and all code inside taken except title taken from https://mui.com/material-ui/react-drawer/*/}
      <Box sx={{flexGrow: 1}}>
        <CssBaseline />
        <AppBar
          position="static"
        >
          <Box sx={{display: 'flex', justifyContent: 'space-between'}}>
            <Toolbar>
              <IconButton
                color="inherit"
                aria-label="toggle drawer"
                edge="start"
                onClick={handleDrawerToggle}
                sx={{mr: 2}}
              >
                <MenuIcon />
              </IconButton>
              {/* Search field and all code taken except title taken from https://mui.com/material-ui/react-app-bar/*/}
              <Search>
                <SearchIconWrapper>
                  <SearchIcon />
                </SearchIconWrapper>
                <StyledInputBase
                  placeholder="Searchâ€¦"
                  inputProps={{'aria-label': 'search'}}
                />
              </Search>
            </Toolbar>
            <Button onClick={logout} href='/Login'>
              <Avatar/></Button>
          </Box>
        </AppBar>
        <Box sx={{bgcolor: '#eff1f1', borderBottom: 1, borderTop: 1}}>
          <Typography variant="h4" sx={{p: 1}}>
            {title}
          </Typography>
        </Box>
        {/* Box taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="nav"
          sx={{width: {sm: drawerWidth}, flexShrink: {sm: 0}}}
          aria-label="mailbox folders"
        >
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            container = {container}
            variant="temporary"
            sx={{
              'display': {xs: 'none', sm: 'block'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
            open={mobileOpen}
            onClose={handleDrawerToggle}
          >
            {drawer}
          </Drawer>
        </Box>
        {/* Box declaration and attributes taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="main"
          sx={{'flexGrow': 1, 'padding-top': 10}}
        >
          <Grid container spacing={1}>
            {mails.map((mail) => (createRow(mail)
            ))}
            {createDisplay()}
          </Grid>
        </Box>
      </Box>
    </div>
  );
}

export default Home;
