import React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import TableContainer from '@mui/material/TableContainer';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import Button from '@mui/material/Button';
import TableCell from '@mui/material/TableCell';
import Stack from '@mui/material/Stack';
import Avatar from '@mui/material/Avatar';
import {useState} from 'react';


const drawerWidth = 200;

const fetchMails = (setMails, setError, mailbox) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/Mailbox?mailbox='+mailbox, {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};

/**
 * @param {string} props prop for function
 * @return {object} JSX Table
 */
function Home(props: Props) {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');

  const {window} = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [inbox, setInbox] = useState('Inbox');
  const [title, setTitle] = useState('Inbox');
  /*
  const [display, setDisplay] = useState(false);
  const [subject, setSubject] = useState('');
  const [from, setFrom] = useState('');
  const [received, setReceived] = useState('');
  const [content, setContent] = useState('');
  const [fromAddress, setFromAddress] = useState('');
  const [toAddress, setToAddress] = useState('');
  const [to, setTo] = useState('');
*/
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError, 'Inbox');
  }, []);

  // Handles Inbox press, updates category and title
  const handleInbox = () => {
    setInbox('Inbox');
    setTitle('Inbox');
    if (inbox==='Trash') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Inbox');
  };
  // Handles Trash press, updates category and title
  const handleTrash = () => {
    setInbox('Trash');
    setTitle('Trash');
    if (inbox==='Inbox') {
      // setDisplay(false);
    }
    fetchMails(setMails, setError, 'Trash');
  };

  // Drawer component on left side, definitions here.
  // Following 25 lines taken from https://mui.com/material-ui/react-drawer/
  const drawer = (
    <div>
      <Toolbar />
      <Divider />
      <List>
        <ListItem key={'Inbox'} disablePadding>
          <ListItemButton onClick={()=>handleInbox()}>
            <ListItemIcon>
              {0 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Inbox'} />
          </ListItemButton>
        </ListItem>
        <ListItem key={'Trash'} disablePadding>
          <ListItemButton onClick={()=>handleTrash()}>
            <ListItemIcon>
              {1 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Trash'} />
          </ListItemButton>
        </ListItem>
      </List>
      <Divider />
    </div>
  );

  // Defines container, the window being populated
  // following 2 lines taken from https://mui.com/material-ui/react-drawer/
  const container = window !== undefined ? () =>
    window().document.body : undefined;

  /**
   * creates  row for the current email being looked at
   * Determines the category the email is in from email.mailbox
   * Has onClick function to display email contents and
   * getReceived() function to determine display dates
   * @param {string} mail email row is being created for
   * @return {string} String containing TableRow elements
   */
  const createRow = (mail) => {
    let item = localStorage.getItem('user');
    item = JSON.parse(item);
    if (item['name'] === mail.mailbox['Name']) {
      return (
        <TableRow key={mail['mail']['received']}>
