import React from 'react';
import './Home.css';

const fetchMails = (setMails, setError) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/Mailbox?mailbox=Inbox', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};

/**
 * @return {object} JSX Table
 */
function Home() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');

  const {window} = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [inbox, setInbox] = useState('Inbox');
  const [title, setTitle] = useState('CSE186 Mail - Inbox');
  const [display, setDisplay] = useState(false);
  const [subject, setSubject] = useState('');
  const [from, setFrom] = useState('');
  const [received, setReceived] = useState('');
  const [content, setContent] = useState('');
  const [fromAddress, setFromAddress] = useState('');
  const [toAddress, setToAddress] = useState('');
  const [to, setTo] = useState('');
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  // Following code reorders emails into most recent
  let i = 0;
  let j = 0;
  for (i=0; i < emails.length-1; i++) {
    for (j=0; j < emails.length-i-1; j++) {
      if (emails[j].received<emails[j+1].received) {
        const temp = emails[j];
        emails[j] = emails[j+1];
        emails[j+1] = temp;
      }
    }
  }
  // Taken code for handling escape function key press from
  // https://stackoverflow.com/questions/37440408/how-to-detect-esc-key-press-in-react-and-how-to-handle-it/57613476#57613476]
  const escFunction = useCallback((event) => {
    if (event.key === 'Escape') {
      setDisplay(false);
    }
  }, []);
  useEffect(() => {
    document.addEventListener('keydown', escFunction, false);
    return () => {
      document.removeEventListener('keydown', escFunction, false);
    };
  });

  // Handles Inbox press, updates category and title
  const handleInbox = () => {
    setInbox('Inbox');
    setTitle('CSE186 Mail - Inbox');
    if (inbox==='Trash') {
      setDisplay(false);
    }
  };
  // Handles Trash press, updates category and title
  const handleTrash = () => {
    setInbox('Trash');
    setTitle('CSE186 Mail - Trash');
    if (inbox==='Inbox') {
      setDisplay(false);
    }
  };
  // Drawer component on left side, definitions here.
  // Following 25 lines taken from https://mui.com/material-ui/react-drawer/
  const drawer = (
    <div>
      <Toolbar />
      <Divider />
      <List>
        <ListItem key={'Inbox'} disablePadding>
          <ListItemButton onClick={()=>handleInbox()}>
            <ListItemIcon>
              {0 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Inbox'} />
          </ListItemButton>
        </ListItem>
        <ListItem key={'Trash'} disablePadding>
          <ListItemButton onClick={()=>handleTrash()}>
            <ListItemIcon>
              {1 % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={'Trash'} />
          </ListItemButton>
        </ListItem>
      </List>
      <Divider />
    </div>
  );

  // Defines container, the window being populated
  // following 2 lines taken from https://mui.com/material-ui/react-drawer/
  const container = window !== undefined ? () =>
    window().document.body : undefined;

  /**
   * Creates display to show email contents when clicking on specific
   * email. Code for box and appbar is influenced by the code found on
   * https://mui.com/material-ui/react-drawer/
   * Similar style to title appbar and box
   * @return {*} String containing display elements
   */
  const createDisplay = () => {
    return (
      <Box z-index='appBar'
        hidden={!display}
        sx={{
          flexgrow: 1,
          height: '50%',
          width: {sm: `calc(100% - ${drawerWidth}px)`},
          ml: {sm: `${drawerWidth}px`},
          position: 'fixed',
          bottom: 0,
          right: 0,
          backgroundColor: 'white'}}>
        <AppBar
          sx={{
            flexgrow: 1,
            height: '60px',
            width: {sm: `calc(100% - ${drawerWidth}px)`},
            ml: {sm: `${drawerWidth}px`},
            position: 'fixed',
            top: '45%',
            right: 0,
            backgroundColor: 'primary.dark'}}
        >
          <Toolbar>
            <Typography variant='h4' component='div'
              sx={{flexGrow: 1,
                color: 'white'}} display='inline'>
              {subject}
            </Typography>
            <IconButton
              size='large'
              color='inherit'
              aria-label='close desktop reader'
              onClick={()=>setDisplay(false)}><ClearIcon/></IconButton>
          </Toolbar>
        </AppBar>
        <Typography variant='body1'
          sx={{color: 'white'}}>
          {'From: ' + from + ' (' + fromAddress + ')\n'}
        </Typography>
        <Typography>
          {'To: ' + to + ' (' + toAddress + ')\n'}
        </Typography>
        <Typography>
          {'Subject: ' + subject + '\n'}
        </Typography>
        <Typography>
          {'Received: ' + received + '\n\n'}
        </Typography>
        <Typography>
          {content}
        </Typography>
        <Button
          aria-label='close mobile reader'
          onClick={()=>{
            setDisplay(false);
          }}></Button>
      </Box>
    );
  };
  /**
   * Updates display with the contents of an email
   * Also sets display to true
   * @param {string} email email being used to update display
   * @return {*} Returns nothing, only needs to update display
   */
  const updateDisplay = (email) => {
    setDisplay(true);
    setSubject(email.subject);
    setFrom(email.from.name);
    setFromAddress(email.from.address);
    setTo(email.to.name);
    setToAddress(email.to.address);
    setContent(email.content);
    const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    let e = '';
    const current = new Date(email.received);
    e=months[current.getMonth()]+' '+current.getDate()+', '+
      current.getFullYear()+' @ '+current.getHours()+':'+current.getMinutes();
    setReceived(e);
    return '';
  };
  /**
   * creates  row for the current email being looked at
   * Determines the category the email is in from email.mailbox
   * Has onClick function to display email contents and
   * getReceived() function to determine display dates
   * @param {string} email email row is being created for
   * @return {string} String containing TableRow elements
   */
  const createRow = (email) => {
    if ((inbox === 'Inbox' && email.mailbox === 'inbox') ||
      (inbox === 'Trash' && email.mailbox === 'trash')) {
      return (
        <TableRow key={email.id}>
          <ListItemButton onClick={()=>updateDisplay(email)}>
            <TableCell align = 'right'>
              {email.from.name}
            </TableCell>
            <TableCell align = 'right'>
              {email.subject}
            </TableCell>
          </ListItemButton>
          <TableCell align = 'right'>
            {getReceived(email.received)}</TableCell>
        </TableRow>);
    } else {
      return;
    }
  };

  /**
   * Updates the received date to proper display for table cell
   * @param {string} received Received string to convert to date
   * @return {string} returns string to put in cell.
   */
  const getReceived = (received) => {
    const d = new Date(received);
    const current = new Date();
    const months = [
      'Jan',
      'Feb',
      'Mar',
      'Apr',
      'May',
      'Jun',
      'Jul',
      'Aug',
      'Sep',
      'Oct',
      'Nov',
      'Dec',
    ];
    if (current.getFullYear() - d.getFullYear()>0) {
      return d.getFullYear();
    } else if (current.getMonth() - d.getMonth() !== 0 ||
    current.getDate() - d.getDate() !== 0) {
      if (Number(d.getDate())<10) {
        return months[d.getMonth()] + ' 0' + d.getDate();
      }
      return months[d.getMonth()] + ' ' + d.getDate();
    } else if (Number(d.getHours())<10) {
      return '0'+d.getHours()+':'+d.getMinutes();
    } else {
      return d.getHours()+':'+d.getMinutes();
    }
  };

  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError);
  }, []);

  return (
    <div>
      {/* Box and all code inside taken except title taken from https://mui.com/material-ui/react-drawer/*/}
      <Box sx={{display: 'flex'}}>
        <CssBaseline />
        <AppBar
          position="fixed"
          sx={{
            width: {sm: `calc(100% - ${drawerWidth}px)`},
            ml: {sm: `${drawerWidth}px`},
          }}
        >
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="toggle drawer"
              edge="start"
              onClick={handleDrawerToggle}
              sx={{mr: 2, display: {sm: 'none'}}}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" noWrap component="div">
              {title}
            </Typography>
          </Toolbar>
        </AppBar>
        {/* Box taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="nav"
          sx={{width: {sm: drawerWidth}, flexShrink: {sm: 0}}}
          aria-label="mailbox folders"
        >
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            container={container}
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
            sx={{
              'display': {xs: 'block', sm: 'none'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
          >
            {drawer}
          </Drawer>
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            variant="permanent"
            sx={{
              'display': {xs: 'none', sm: 'block'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
            open
          >
            {drawer}
          </Drawer>
        </Box>
        {/* Box declaration and attributes taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="main"
          sx={{flexGrow: 1, p: 3, width: {sm: `calc(100% - ${drawerWidth}px)`}}}
        >
          <Toolbar />
          <Grid container spacing={0}>
            <TableContainer component={Paper}>
              <Table aria-label='simple table'>
                <TableBody>
                  {emails.map((email) => (createRow(email)
                  ))}
                </TableBody>
              </Table>
              <Button></Button>
            </TableContainer>
            {createDisplay()}
            <input/>
          </Grid>
        </Box>
      </Box>
    </div>
    /*
    <div>
      <h2 id='welcome'>CSE186 Authenticated Book Example</h2>
      <a href='/Login'>Login</a>
      <button disabled={!name} onClick={logout}>Logout</button>
      <label>{name ? name : ''}</label>
      <p/>
      <table id='mails'>
        <thead>
          <tr>
            <th>ISBN</th>
            <th>Title</th>
            <th>Author</th>
            <th>Publisher</th>
          </tr>
        </thead>
        <tbody>
          {mails.map((book) => (
            <tr key={book.isbn} id={'isbn'+book.isbn}>
              <td>{book.isbn}</td>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.publisher}</td>
            </tr>
          ))}
          <tr key={'error'}>
            <td colSpan={4}>{error}</td>
          </tr>
        </tbody>
      </table>
    </div>
    */
  );
}

export default Home;
