import React from 'react';
import './Home.css';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';

const drawerWidth = 200;

const fetchMails = (setMails, setError) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/Mailbox?mailbox=Inbox', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setMails(json);
    })
    .catch((error) => {
      console.log(error);
      setMails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};

/**
 * @return {object} JSX Table
 */
function Home() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [mails, setMails] = React.useState([]);
  const [name, setName] = React.useState(user ? user.name : '');
  const [error, setError] = React.useState('Logged Out');

  const logout = () => {
    localStorage.removeItem('user');
    setMails([]);
    setName('');
    setError('Logged Out');
  };

  React.useEffect(() => {
    fetchMails(setMails, setError);
  }, []);

  return (
    <div>
      {/* Box and all code inside taken except title taken from https://mui.com/material-ui/react-drawer/*/}
      <Box sx={{display: 'flex'}}>
        <CssBaseline />
        <AppBar
          position="fixed"
          sx={{
            width: {sm: `calc(100% - ${drawerWidth}px)`},
            ml: {sm: `${drawerWidth}px`},
          }}
        >
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="toggle drawer"
              edge="start"
              onClick={handleDrawerToggle}
              sx={{mr: 2, display: {sm: 'none'}}}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" noWrap component="div">
              {title}
            </Typography>
          </Toolbar>
        </AppBar>
        {/* Box taken from https://mui.com/material-ui/react-drawer/*/}
        <Box
          component="nav"
          sx={{width: {sm: drawerWidth}, flexShrink: {sm: 0}}}
          aria-label="mailbox folders"
        >
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            container={container}
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
            sx={{
              'display': {xs: 'block', sm: 'none'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
          >
            {drawer}
          </Drawer>
          {/* Drawer taken from https://mui.com/material-ui/react-drawer/*/}
          <Drawer
            variant="permanent"
            sx={{
              'display': {xs: 'none', sm: 'block'},
              '& .MuiDrawer-paper':
              {boxSizing: 'border-box', width: drawerWidth},
            }}
            open
          >
            {drawer}
          </Drawer>
        </Box>
        </Box>
    </div>
    /*
    <div>
      <h2 id='welcome'>CSE186 Authenticated Book Example</h2>
      <a href='/Login'>Login</a>
      <button disabled={!name} onClick={logout}>Logout</button>
      <label>{name ? name : ''}</label>
      <p/>
      <table id='mails'>
        <thead>
          <tr>
            <th>ISBN</th>
            <th>Title</th>
            <th>Author</th>
            <th>Publisher</th>
          </tr>
        </thead>
        <tbody>
          {mails.sort((a, b) => (a.title > b.title) ? 1 : -1).map((book) => (
            <tr key={book.isbn} id={'isbn'+book.isbn}>
              <td>{book.isbn}</td>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.publisher}</td>
            </tr>
          ))}
          <tr key={'error'}>
            <td colSpan={4}>{error}</td>
          </tr>
        </tbody>
      </table>
    </div>
    */
  );
}

export default Home;
